import React, { useState } from 'react';
import FormField from './components/FormField';
import * as gf from './utils/globalFunctions';
//import { formatDateToDDMMMYYYY, getCurrentDateTime } from './utils/globalFunctions';
import { callBackend, fetchAppModules } from './utils/moduleStructureAPI';



function DynamicFormRenderer({ config, dropdowns, module }) {
  if (!Array.isArray(config)) {
    console.error(`‚ùå Invalid config passed to DynamicFormRenderer:`, config);
    return <div className="text-red-600">Unable to load form configuration.</div>;
  }

  // Filter out fields hidden in app
  const visibleFields = config.filter(f => !!f.showInApp);
  //const visibleFields = config.filter(field => String(field.showInApp).toLowerCase() === 'true');

  // Group by formRow and sort by formColumn
  const groupedFields = {};
  visibleFields.forEach(field => {
    const row = field.formRow || 0;
    if (!groupedFields[row]) groupedFields[row] = [];
    groupedFields[row].push(field);
  });

  const sortedRowKeys = Object.keys(groupedFields).sort((a, b) => Number(a) - Number(b));

  // Initial state
  const initialState = visibleFields.reduce((acc, field) => {
    if (field.dataType === 'date') {
    // Default to today's date in yyyy-mm-dd format
    const today = new Date().toISOString().split('T')[0];
    acc[field.key] = today;
  } else {
    acc[field.key] = '';
  }
  return acc;
}, {});

  const [formData, setFormData] = useState(initialState);
  const [errors, setErrors] = useState({});

  const handleChange = (key, value) => {
    setFormData(prev => ({ ...prev, [key]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = {};

    visibleFields.forEach(field => {
      if ((field.isRequired === true || String(field.isRequired).toLowerCase() === 'true') 
        && !formData[field.key]) {
      newErrors[field.key] = `${field.label || field.key} is required`;
      }
    });

    setErrors(newErrors);
    if (Object.keys(newErrors).length > 0) return;

    // ‚úÖ Enrich with user info and timestamps
    const user = JSON.parse(sessionStorage.getItem('user'));
    const username = user?.username || 'Unknown';

    const enrichedData = { ...formData };

    config.forEach(field => {
      if (field.dataType === 'date' && enrichedData[field.key]) {
        enrichedData[field.key] = gf.formatDateToDDMMMYYYY(enrichedData[field.key]);
      }
    });

    enrichedData.createdBy = username;
    enrichedData.modifiedBy = username;
    enrichedData.createdOn = gf.getCurrentDateTime();
    enrichedData.modifiedOn = enrichedData.createdOn;

    // ‚úÖ Get sheetId
    let appModulesCache = JSON.parse(sessionStorage.getItem('appModulesCache'));
    if (!appModulesCache || !appModulesCache.data) {
      await fetchAppModules(true);
      appModulesCache = JSON.parse(sessionStorage.getItem('appModulesCache'));
    }

    const matched = appModulesCache?.data?.find(group =>
      group.modules.some(mod => mod.module === module)
    );

    const sheetId = matched?.modules.find(m => m.module === module)?.sheetId;
    if (!sheetId) {
      console.error(`‚ùå No sheetId found for module ${module}`);
      alert('Error: Unable to find sheetId for submission.');
      return;
    }

    // ‚úÖ Submit to backend
    try {
      console.log('üì§ Payload being sent to backend:', {
  module,
  sheetId,
  tab: 'master',
  entry: enrichedData
});

      await callBackend('saveLogEntry', {module,
        sheetId,
        tab: 'master',
        entry: enrichedData
      });

      alert('‚úÖ Form submitted!');
      setFormData(initialState);
    } catch (err) {
      console.error('‚ùå Submission failed:', err);
      alert('Failed to submit. Please try again.');
    }
  };


  return (
    <form onSubmit={handleSubmit} className="jp-form-area space-y-6">
      {/* Sticky Submit Button */}
      <div className="jp-form-header shadow-md">
        <button type="submit" className="btn-primary">Submit</button>
      </div>

      {sortedRowKeys.map(rowKey => {
        const rowFields = groupedFields[rowKey].sort(
          (a, b) => (Number(a.formColumn) || 0) - (Number(b.formColumn) || 0)
        );

        return (
          <div key={rowKey} className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {rowFields.map(field => (
              <FormField
                key={field.key}
                field={field}
                value={formData[field.key]}
                error={errors[field.key]}
                options={dropdowns[field.key] || []}
                onChange={(val) => handleChange(field.key, val)}
              />
            ))}
          </div>
        );
      })}
    </form>
  );
}

export default DynamicFormRenderer;
