// src/App.js
import React, { useMemo } from "react";
import { Routes, Route, Navigate, Link, Outlet, useNavigate } from "react-router-dom";
import { LogOut, Menu } from "lucide-react";
import Login from "./Login";
import { useIsMobile } from "./packages/ui/use-mobile";
import logo from "./appImages/justpressedLogo.png";

// Module screens (you'll create these stubs below)
import Dashboard from "./modules/Dashboard";
import Production from "./modules/Production";
import Bottling from "./modules/Bottling";
import Inventory from "./modules/Inventory";
import Cash from "./modules/Cash";

const NAV_ITEMS = [
  { path: "/dashboard", label: "Dashboard" },
  { path: "/production", label: "Production" },
  { path: "/bottling", label: "Bottling" },
  { path: "/inventory", label: "Inventory" },
  { path: "/cash", label: "Cash" },
];

function App() {
 const [user, setUser] = React.useState(() => {
   const stored = sessionStorage.getItem("user");
   return stored ? JSON.parse(stored) : null;
 });

  // If NOT logged in: only show Login
  if (!user) {
    return (
      <Routes>
        <Route path="*" element={<Login setUser={setUser} />} />
      </Routes>
    );
  }

  // Logged in: render app layout + module routes
  return (
    <Routes>
      <Route element={<AppLayout user={user} />}>
        <Route index element={<Navigate to="/dashboard" replace />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/production" element={<Production />} />
        <Route path="/bottling" element={<Bottling />} />
        <Route path="/inventory" element={<Inventory />} />
        <Route path="/cash" element={<Cash />} />
        <Route path="*" element={<Navigate to="/dashboard" replace />} />
      </Route>
    </Routes>
  );
}

function AppLayout({ user }) {
  const navigate = useNavigate();
  const isMobile = useIsMobile();
  const [open, setOpen] = React.useState(false);

 const logout = () => {
   sessionStorage.clear();
   setUser(null);
   navigate("/login", { replace: true });
 };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* Sidebar */}
      {/* Desktop: sticky left rail | Mobile: left drawer */}
        {/* Mobile drawer (left) */}
    <div className={[
    "fixed inset-y-0 left-0 z-40 w-64 bg-white border-r p-2 transition-transform duration-200 ease-in-out",
    "md:hidden",
    open ? "translate-x-0 shadow-lg" : "-translate-x-full",
            ].join(" ")}>
        <div className="h-16 border-b flex items-center px-4">
          <img src={logo} alt="Just Pressed" className="h-10 w-auto" />
        </div>
        <nav className="p-2 space-y-1">
          {NAV_ITEMS.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className="block px-3 py-2 rounded-md text-gray-700 hover:bg-gray-100"
              onClick={() => isMobile && setOpen(false)}
            >
              {item.label}
            </Link>
          ))}
        </nav>
      </div>

      {/* Page content */}
      <div className="flex-1 min-w-0 flex flex-col">
        {/* Top bar */}
 <header className="bg-white border-b">
   <div className="h-16 px-4 container mx-auto flex items-center justify-between">
     <div className="flex items-center gap-3">
       {/* Mobile menu button (LEFT) */}
       <button onClick={() => setOpen(v => !v)} className="md:hidden p-2 rounded-md hover:bg-gray-100" aria-label="Toggle menu">
         <Menu size={20} />
       </button>
       {/* Brand logo (replace the text) */}
       <img src={logo} alt="Just Pressed" className="h-8 w-auto" />
       {/* Horizontal nav (desktop/tablet) */}
       <nav className="hidden md:flex items-center gap-1 ml-4">
         {NAV_ITEMS.map(item => (
           <NavLink
             key={item.path}
             to={item.path}
             className={({ isActive }) =>
               [
                 "px-3 py-1.5 rounded-lg text-sm font-medium",
                 isActive ? "bg-blue-600 text-white" : "text-gray-700 hover:bg-gray-100"
               ].join(" ")
             }
           >
             {item.label}
           </NavLink>
         ))}
       </nav>
     </div>
     <div className="flex items-center gap-3">
       <span className="text-sm text-gray-600">{user?.fullName || user?.username}</span>
       <button onClick={logout} title="Logout" className="p-2 rounded-md hover:bg-red-50 text-red-600">
         <LogOut size={18} />
       </button>
     </div>
   </div>
 </header>

        {/* Mobile overlay when drawer is open */}
        {isMobile && open && (
          <button
            className="fixed inset-0 bg-black/30 z-30 md:hidden"
            onClick={() => setOpen(false)}
            aria-label="Close menu"
          />
        )}

        {/* Routed content */}
        <main className="p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
}

export default App;
