import React from "react";
import { Factory } from "lucide-react";
import { useMasterForm } from "../hooks/useMasterForm";
import DynamicMasterForm from "../components/DynamicMasterForm";

export default function Production() {
  // Custom rule: seedInputKgs >= 1.1 * (unfilteredOilKgs + cakeKgs)
  const validateProduction = (v) => {
    const input = Number(v.seedInputKgs);
    const oil = Number(v.unfilteredOilKgs);
    const cake = Number(v.cakeKgs);
    if ([input, oil, cake].some(Number.isNaN)) return null;
    const minReq = 1.1 * (oil + cake);
    if (input < minReq) {
      return `Seed Inputs (${input} kg) must be at least ${minReq.toFixed(2)} kg (1.1 × (${oil} + ${cake})).`;
    }
    return null;
  };

  const { schema, values, setField, errors, loading, submitting, success, submit } =
    useMasterForm("Production", { customValidate: validateProduction });

  return (
    <DynamicMasterForm
      title="Production Data Entry"
      icon={Factory}
      schema={schema}
      values={values}
      setField={setField}
      errors={errors}
      loading={loading}
      submitting={submitting}
      success={success}
      onSubmit={() => submit()}
    />
  );
}
