// src/DynamicFormRenderer.js
import React, { useEffect, useState } from 'react';
import FormField from './components/FormField';
import Loader from './components/Loader';
import * as dateUtils from './utils/dateUtils.js';
import { validateForm } from './utils/formValidation';
import { callBackend, fetchAppModules } from './utils/moduleStructureAPI';
import { getOptionsFromModuleSource } from './utils/dropdownProvider';

function DynamicFormRenderer({ config, dropdowns, module }) {
  if (!Array.isArray(config)) {
    console.error(`‚ùå Invalid config passed to DynamicFormRenderer:`, config);
    return <div className="text-red-600">Unable to load form configuration.</div>;
  }

  // Only fields marked visible
  const visibleFields = config.filter(f => !!f.showInApp);

  // Group fields by formRow
  const groupedFields = {};
  visibleFields.forEach(field => {
    const row = field.formRow || 0;
    if (!groupedFields[row]) groupedFields[row] = [];
    groupedFields[row].push(field);
  });
  const sortedRowKeys = Object.keys(groupedFields).sort((a, b) => Number(a) - Number(b));

  // Initial state (dates default to yyyy-mm-dd)
  const initialState = visibleFields.reduce((acc, field) => {
    acc[field.key] = field.dataType === 'date'
      ? new Date().toISOString().split('T')[0]
      : '';
    return acc;
  }, {});

  const [formData, setFormData] = useState(initialState);
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Runtime dropdowns for module-sourced options (e.g., "product.skuId")
  const [runtimeDropdowns, setRuntimeDropdowns] = useState({}); // { [key]: string[] }
  const [loadingDD, setLoadingDD] = useState({});               // { [key]: boolean }

  // Resolve module-based dropdowns on mount/config change
  useEffect(() => {
    let alive = true;

    async function ensureOptions(field) {
      if (field.inputType !== 'dropdown') return;

      const src = field.dropdownSource;
      if (!src || src === 'sheet') return;         // BE-provided dropdowns
      if (!src.includes('.')) return;              // not a module.prop pattern
      if (runtimeDropdowns[field.key]?.length) return; // already loaded

      setLoadingDD(prev => ({ ...prev, [field.key]: true }));
      try {
        const opts = await getOptionsFromModuleSource(src);
        if (!alive) return;
        setRuntimeDropdowns(prev => ({ ...prev, [field.key]: opts }));
      } catch (e) {
        console.error(`Failed to load dropdown for ${field.key} from ${src}:`, e);
        if (!alive) return;
        setRuntimeDropdowns(prev => ({ ...prev, [field.key]: [] }));
      } finally {
        if (alive) setLoadingDD(prev => ({ ...prev, [field.key]: false }));
      }
    }

    visibleFields.forEach(ensureOptions);
    return () => { alive = false; };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [JSON.stringify(visibleFields)]);

  const handleChange = (key, value) => {
    setFormData(prev => ({ ...prev, [key]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate (common + module-specific)
    const newErrors = validateForm(module, visibleFields, formData);
    setErrors(newErrors);
    if (Object.keys(newErrors).length > 0) return;

    setIsSubmitting(true);
    try {
      // Enrich with user + timestamps
      const user = JSON.parse(sessionStorage.getItem('user'));
      const username = user?.username || 'Unknown';

      const enrichedData = { ...formData };
      config.forEach(field => {
        if (field.dataType === 'date' && enrichedData[field.key]) {
          enrichedData[field.key] = dateUtils.formatDateToDDMMMYYYY(enrichedData[field.key]);
        }
      });
      enrichedData.createdBy = username;
      enrichedData.modifiedBy = username;
      enrichedData.createdOn = dateUtils.getCurrentDateTime();
      enrichedData.modifiedOn = enrichedData.createdOn;

      // Find sheetId
      let appModulesCache = JSON.parse(sessionStorage.getItem('appModulesCache'));
      if (!appModulesCache || !appModulesCache.data) {
        await fetchAppModules(true);
        appModulesCache = JSON.parse(sessionStorage.getItem('appModulesCache'));
      }
      const matched = appModulesCache?.data?.find(group =>
        group.modules.some(mod => mod.module === module)
      );
      const sheetId = matched?.modules.find(m => m.module === module)?.sheetId;
      if (!sheetId) {
        console.error(`‚ùå No sheetId found for module ${module}`);
        alert('Error: Unable to find sheetId for submission.');
        return;
      }

      // Submit
      console.log('üì§ Payload being sent to backend:', {
        module, sheetId, tab: 'master', entry: enrichedData
      });

      await callBackend('saveLogEntry', {
        module, sheetId, tab: 'master', entry: enrichedData
      });

      alert('‚úÖ Form submitted!');
      setFormData(initialState);
      setErrors({});
    } catch (err) {
      console.error('‚ùå Submission failed:', err);
      alert('Failed to submit. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="jp-form-area space-y-6" aria-busy={isSubmitting}>
      {/* Sticky Submit Button */}
      <div className="jp-form-header shadow-md">
        <button type="submit" className="btn-primary" disabled={isSubmitting}>
          {isSubmitting ? (
            <span className="inline-flex items-center gap-2">
              <span className="animate-spin h-4 w-4 rounded-full border-2 border-gray-300 border-t-transparent" />
              Submitting‚Ä¶
            </span>
          ) : (
            'Submit'
          )}
        </button>
      </div>

      {/* Blocking overlay while submitting */}
      {isSubmitting && (
        <div className="fixed inset-0 bg-black/10 backdrop-blur-[1px] z-50 flex items-center justify-center">
          <Loader message="dataBeingSubmitted" />
        </div>
      )}

      <fieldset disabled={isSubmitting} className="space-y-6">
        {sortedRowKeys.map(rowKey => {
          const rowFields = groupedFields[rowKey].sort(
            (a, b) => (Number(a.formColumn) || 0) - (Number(b.formColumn) || 0)
          );

          return (
            <div key={rowKey} className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {rowFields.map(field => {
                // Decide dropdown options
                let options = [];
                let loadingOptions = false;

                if (field.inputType === 'dropdown') {
                  if (field.dropdownSource === 'sheet') {
                    // From BE dropdowns tab
                    options = dropdowns[field.key] || [];
                  } else if (field.dropdownSource?.includes('.')) {
                    // From master module dump
                    options = runtimeDropdowns[field.key] || [];
                    loadingOptions = !!loadingDD[field.key];
                  }
                }

                return (
                  <FormField
                    key={field.key}
                    field={field}
                    value={formData[field.key]}
                    error={errors[field.key]}
                    options={options}
                    onRequestOptions={(f) => getOptionsFromModuleSource(f?.dropdownSource)}
                    onChange={(val) => handleChange(field.key, val)}
                  />
                );
              })}
            </div>
          );
        })}
      </fieldset>
    </form>
  );
}

export default DynamicFormRenderer;
