// src/components/FormField.js
import React, { useEffect, useRef, useState } from 'react';
import { Input } from "../packages/ui/input";
import { Label } from "../packages/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../packages/ui/select";

function FormField({ field, value, onChange, options = [], error, onDropdownOpen }) {
  const { key, label, placeholderText, inputType, dataType } = field;
  const inputId = key;
  const [localOptions, setLocalOptions] = useState(options);
  const [loading, setLoading] = useState(false);
  const inFlight = useRef(null);

  useEffect(() => {
    setLocalOptions(options || []);
  }, [options]);

  const ensureOptions = async () => {
    if (localOptions.length > 0 || !onDropdownOpen) return;
    if (inFlight.current) return;
    setLoading(true);
    try {
      inFlight.current = onDropdownOpen(); // returns Promise<string[]>
      const fresh = await inFlight.current;
      setLocalOptions(Array.isArray(fresh) ? fresh : []);
    } catch {
      setLocalOptions([]);
    } finally {
      inFlight.current = null;
      setLoading(false);
    }
  };

  const renderInput = () => {
    switch (inputType) {
      case 'dropdown':
        return (
          <select
            id={inputId}
            className="jp-select"
            value={value}
            onFocus={ensureOptions}
            onMouseDown={ensureOptions}
            onChange={(e) => onChange(e.target.value)}
            aria-busy={loading ? 'true' : 'false'}
            aria-invalid={error ? 'true' : 'false'}
            disabled={field.isDisabled}
          >
            <option value="">
              {loading ? 'Fetching dropdown options…' : (placeholderText || `Select ${label}`)}
            </option>
            {localOptions.map((opt) => (
              <option key={opt} value={opt}>{opt}</option>
            ))}
          </select>
        );
      case 'textarea':
        return (
          <textarea
            id={inputId}
            className="jp-input"
            placeholder={placeholderText || label}
            value={value}
            onChange={(e) => onChange(e.target.value)}
            aria-invalid={error ? 'true' : 'false'}
            rows={3}
            disabled={field.isDisabled}
          />
        );
      case 'radio':
        return (
          <div className="flex gap-4 mt-1">
            {localOptions.map((opt) => (
              <label key={opt} className="flex items-center gap-1 text-sm">
                <input
                  type="radio"
                  value={opt}
                  checked={value === opt}
                  onChange={(e) => onChange(e.target.value)}
                  className="jp-radio"
                />
                {opt}
              </label>
            ))}
          </div>
        );
      default:
        return (
          <input
            type={inputType || (dataType === 'number' ? 'number' : 'text')}
            id={inputId}
            className="jp-input"
            placeholder={placeholderText || label}
            value={value}
            onChange={(e) => onChange(e.target.value)}
            aria-invalid={error ? 'true' : 'false'}
            disabled={field.isDisabled}
          />
        );
    }
  };

  return (
    <div className="jp-group">
      <label htmlFor={inputId} className="jp-label">{label}</label>
      {renderInput()}
      {error && <p className="jp-help">{error}</p>}
    </div>
  );
}

export default FormField;
