// src/utils/dropdownProvider.js
import { fetchMasterData } from './moduleMasterAPI'; // NOTE: exact filename + casing!

function getDump(module) {
  const raw = sessionStorage.getItem(`dump_${module}`);
  if (!raw) return null;
  try { return JSON.parse(raw); } catch { return null; }
}

export async function getOptionsFromModuleSource(source) {
  const [module, prop] = String(source).split('.');
  console.debug('[dropdownProvider] called with source:', source, '→ module:', module, 'prop:', prop);

  if (!module || !prop) {
    console.warn('[dropdownProvider] Bad source:', source);
    return [];
  }

  // 1) try cache
  let dump = getDump(module);
  console.debug('[dropdownProvider] cache hit?', !!dump);

  // 2) fetch if not cached
  if (!dump) {
    console.debug('[dropdownProvider] fetching dump for module:', module);
    dump = await fetchMasterData(module); // expected: { headers, rows, totalRecords }
    sessionStorage.setItem(`dump_${module}`, JSON.stringify(dump));
  }

  const rows = dump?.rows || [];
  console.debug('[dropdownProvider] rows length:', rows.length);
  if (rows[0]) console.debug('[dropdownProvider] first row keys:', Object.keys(rows[0]));

  const uniq = new Set();
  for (const r of rows) {
    const v = r?.[prop];
    if (v !== undefined && v !== null && `${v}`.trim() !== '') uniq.add(String(v));
  }

  const out = Array.from(uniq).sort((a, b) => a.localeCompare(b));
  console.debug('[dropdownProvider] options built:', out.length, out.slice(0, 5));
  return out;
}
