// src/ModuleManagement.js
import React, { useEffect, useState } from 'react';
import { fetchMasterData } from './utils/moduleMasterAPI';
import { paginate } from './utils/paginate';
import Loader from './components/Loader'; 

function ModuleManagement({ module }) {
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1);
  const [limit] = useState(50);
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

 const loadData = async (currentPage = 1) => {
    const loginStart = performance.now(); // ✅ start timer
    try {
      setLoading(true);
      const result = await fetchMasterData(module, currentPage);
      const loginEnd = performance.now(); // ✅ end timer
      setData(result.data || []);
      setPage(result.page || 1);
      setTotalPages(result.totalPages || 1);
      setMessage(result.message || '');
      console.log(`⏱ ${module} API took: ${(loginEnd - loginStart).toFixed(2)} ms`);
    } catch (err) {
      console.error(`Failed to load ${module} data:`, err);
      setMessage('Failed to load data.');
    } finally {
      setLoading(false);
    }
  };


  useEffect(() => {
    if (module) loadData(page);
  }, [module, page]);

  return (
    <div className="content-panel">
      {/* <h2 className="content-header">{module.charAt(0).toUpperCase() + module.slice(1)} Management</h2> */}

      {loading ? (
         <Loader message={`Relax, fetching ${module} data...`} />
      ) : message ? (
        <p className="text-center text-gray-600">{message}</p>
      ) : (
        <>
        <div className="table-wrapper">
          <table className="table-auto-width">
            <thead className="table-header-bg">
              <tr>
                {Object.keys(data?.[0] || {}).map((key) => (
                  <th key={key} className="table-header-text">{key}</th>
                ))}
              </tr>
            </thead>
            <tbody className="table-body-bg">
              {data.map((row, index) => (
                <tr key={index} className="table-row-border">
                  {Object.values(row).map((val, i) => (
                    <td key={i} className="table-cell-text">{val}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
          </div>

          {/* Pagination Controls */}
            {totalPages > 1 && (
                <div className="flex justify-between items-center mt-4">
                    <button
                    className="btn-secondary"
                    onClick={() => setPage(prev => Math.max(prev - 1, 1))}
                    disabled={page === 1}
                    >
                    Previous
                    </button>

                    <span className="text-sm font-medium">
                    Page {page} of {totalPages}
                    </span>

                    <button
                    className="btn-secondary"
                    onClick={() => setPage(prev => Math.min(prev + 1, totalPages))}
                    disabled={page === totalPages}
                    >
                    Next
                    </button>
                </div>
                )}
        </>
      )}
    </div>
  );
}

export default ModuleManagement;
