import React from 'react';

function FormField({ field, value, onChange, options = [], error, loadingOptions = false }) {
  const { key, label, placeholderText, inputType, dataType } = field;


  const baseClass = 'jp-input';
  const selectClass = `jp-select ${error ? 'border-red-500' : ''}`;
  const errorClass = error ? 'border-red-500' : '';
  const inputClass = `${baseClass} ${errorClass}`;

  const renderInput = () => {
    switch (inputType) {
      case 'dropdown':
        return (
          <select
            className={selectClass}
            value={value}
            onChange={(e) => onChange(e.target.value)}
            disabled={loadingOptions}
          >
            <option value="">
              {loadingOptions ? 'Fetching dropdown options…' : (placeholderText || `Select ${label}`)}
            </option>
            {!loadingOptions && options.map((opt) => (
              <option key={opt} value={opt}>{opt}</option>
            ))}
          </select>
        );
      case 'textarea':
        return (
          <textarea
            className={inputClass}
            placeholder={placeholderText || label}
            value={value}
            onChange={(e) => onChange(e.target.value)}
            rows={3}
          />
        );
      case 'radio':
        return (
          <div className="flex gap-4 mt-1">
            {options.map((opt) => (
              <label key={opt} className="flex items-center gap-1 text-sm">
                <input
                  type="radio"
                  value={opt}
                  checked={value === opt}
                  onChange={(e) => onChange(e.target.value)}
                  className="jp-radio"
                />
                {opt}
              </label>
            ))}
          </div>
        );
      default:
        return (
          <input
            type={inputType || (dataType === 'number' ? 'number' : 'text')}
            className={inputClass}
            placeholder={placeholderText || label}
            value={value}
            onChange={(e) => onChange(e.target.value)}
          />
        );
    }
  };

  return (
    <div className="form-group">
      <label htmlFor={key} className="jp-label">{label}</label>
      {renderInput()}
      {error && <p className="text-sm text-red-600 mt-1">{error}</p>}
    </div>
  );
}

export default FormField;
