// src/App.js
import React, { useMemo } from "react";
import { Routes, Route, Navigate, Link, Outlet, useNavigate } from "react-router-dom";
import { LogOut, Menu } from "lucide-react";
import Login from "./Login";
import { useIsMobile } from "./ui/use-mobile";

// Module screens (you'll create these stubs below)
import Dashboard from "./modules/Dashboard";
import Production from "./modules/Production";
import Bottling from "./modules/Bottling";
import Inventory from "./modules/Inventory";
import Cash from "./modules/Cash";

const NAV_ITEMS = [
  { path: "/dashboard", label: "Dashboard" },
  { path: "/production", label: "Production" },
  { path: "/bottling", label: "Bottling" },
  { path: "/inventory", label: "Inventory" },
  { path: "/cash", label: "Cash" },
];

function App() {
  const user = useMemo(() => {
    const stored = sessionStorage.getItem("user");
    return stored ? JSON.parse(stored) : null;
  }, []);

  // If NOT logged in: only show Login
  if (!user) {
    return (
      <Routes>
        <Route path="*" element={<Login />} />
      </Routes>
    );
  }

  // Logged in: render app layout + module routes
  return (
    <Routes>
      <Route element={<AppLayout user={user} />}>
        <Route index element={<Navigate to="/dashboard" replace />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/production" element={<Production />} />
        <Route path="/bottling" element={<Bottling />} />
        <Route path="/inventory" element={<Inventory />} />
        <Route path="/cash" element={<Cash />} />
        <Route path="*" element={<Navigate to="/dashboard" replace />} />
      </Route>
    </Routes>
  );
}

function AppLayout({ user }) {
  const navigate = useNavigate();
  const isMobile = useIsMobile();
  const [open, setOpen] = React.useState(false);

  const logout = () => {
    sessionStorage.clear();
    navigate("/login", { replace: true });
  };

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Sidebar */}
      {/* Desktop: sticky left rail | Mobile: left drawer */}
      <div
        className={[
          "bg-white border-r w-64 shrink-0 z-40",
          "md:static md:translate-x-0 md:shadow-none",
          // Mobile drawer from LEFT
          "fixed inset-y-0 left-0 transition-transform duration-200 ease-in-out md:w-64",
          open ? "translate-x-0 shadow-lg" : "-translate-x-full md:translate-x-0",
        ].join(" ")}
      >
        <div className="h-16 border-b flex items-center px-4">
          <img src="/appImages/justpressedLogo.png" alt="Just Pressed" className="h-10 w-auto" />
        </div>
        <nav className="p-2 space-y-1">
          {NAV_ITEMS.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className="block px-3 py-2 rounded-md text-gray-700 hover:bg-gray-100"
              onClick={() => isMobile && setOpen(false)}
            >
              {item.label}
            </Link>
          ))}
        </nav>
      </div>

      {/* Page content */}
      <div className="flex-1 min-w-0 flex flex-col">
        {/* Top bar */}
        <header className="h-16 bg-white border-b flex items-center justify-between px-4">
          <div className="flex items-center gap-2">
            {/* Mobile menu button (LEFT) */}
            <button
              onClick={() => setOpen((v) => !v)}
              className="md:hidden p-2 rounded-md hover:bg-gray-100"
              aria-label="Toggle menu"
            >
              <Menu size={20} />
            </button>
            <h1 className="text-lg font-semibold">Just Pressed Ops</h1>
          </div>
          <div className="flex items-center gap-3">
            <span className="text-sm text-gray-600">{user?.fullName || user?.username}</span>
            <button
              onClick={logout}
              title="Logout"
              className="p-2 rounded-md hover:bg-red-50 text-red-600"
            >
              <LogOut size={18} />
            </button>
          </div>
        </header>

        {/* Mobile overlay when drawer is open */}
        {isMobile && open && (
          <button
            className="fixed inset-0 bg-black/30 z-30 md:hidden"
            onClick={() => setOpen(false)}
            aria-label="Close menu"
          />
        )}

        {/* Routed content */}
        <main className="p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
}

export default App;
