import React from "react";
import { Button } from "../packages/ui/button";
import { Input } from "../packages/ui/input";
import { Label } from "../packages/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../packages/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "../packages/ui/card";
import { AlertCircle, CheckCircle } from "lucide-react";

export default function DynamicMasterForm({ title, icon: Icon, schema, values, setField, errors, loading, submitting, success, onSubmit }) {
  if (loading || !schema) {
    return <div className="max-w-4xl mx-auto p-6">Loading formâ€¦</div>;
  }

  const fields = schema.fields;           // visible fields only (already filtered in hook)
  const optionsMap = schema.options || {}; // key â†’ array

  // Simple 2-column like Figma, but you can read formRow/formColumn if you want exact placement
  return (
    <Card className="max-w-4xl mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          {Icon ? <Icon size={20} className="text-blue-600" /> : null}
          {title}
        </CardTitle>
      </CardHeader>

      <CardContent>
        {success && (
          <div className="mb-4 p-3 bg-green-50 border border-green-200 rounded-md flex items-center gap-2 text-green-700">
            <CheckCircle size={16} />
            Saved successfully!
          </div>
        )}

        <form onSubmit={(e) => { e.preventDefault(); onSubmit(); }} className="space-y-4">
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            {fields.map((f) => {
              const common = {
                key: f.key,
                label: f.label || f.key,
                placeholder: f.placeholder || "",
                required: f.required,
                disabled: f.isDisabled,
              };

              if ((f.inputType || "").toLowerCase() === "dropdown") {
                const opts = optionsMap[f.key] || [];
                return (
                  <div key={f.key} className="space-y-2">
                    <Label htmlFor={f.key}>{common.label}</Label>
                    <Select
                      value={String(values[f.key] ?? "")}
                      onValueChange={(v) => setField(f.key, v)}
                      disabled={common.disabled}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder={common.placeholder || `Select ${common.label.toLowerCase()}`} />
                      </SelectTrigger>
                      <SelectContent>
                        {opts.map((o) => (
                          <SelectItem key={String(o)} value={String(o)}>{String(o)}</SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    {errors[f.key] && <p className="text-red-600 text-sm">{errors[f.key]}</p>}
                  </div>
                );
              }

              // default to text/number
              const isNum = /^float|^number|^int/i.test(String(f.dataType));
              return (
                <div key={f.key} className="space-y-2">
                  <Label htmlFor={f.key}>{common.label}</Label>
                  <Input
                    id={f.key}
                    type={isNum ? "number" : (f.inputType || "text")}
                    inputMode={isNum ? "decimal" : undefined}
                    step={isNum ? "0.01" : undefined}
                    value={String(values[f.key] ?? "")}
                    onChange={(e) => setField(f.key, e.target.value)}
                    placeholder={common.placeholder}
                    disabled={common.disabled}
                  />
                  {errors[f.key] && <p className="text-red-600 text-sm">{errors[f.key]}</p>}
                </div>
              );
            })}
          </div>

        {/* form-level error */}
        {errors.validation && (
          <div className="p-3 bg-red-50 border border-red-200 rounded-md flex items-center gap-2 text-red-700">
            <AlertCircle size={16} />
            {errors.validation}
          </div>
        )}

          <Button type="submit" className="w-full" disabled={submitting}>
            {submitting ? "Submitting..." : "Submit"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}
